mapper.update:
UPDATE data_source
name = #{name,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR,typeHandler=org.apache.ibatis.type.EnumTypeHandler}, host = #{host,jdbcType=VARCHAR}, port = #{port,jdbcType=INTEGER}, database_name = #{databaseName,jdbcType=VARCHAR}, username = #{username,jdbcType=VARCHAR}, encrypted_password = #{encryptedPassword,jdbcType=VARCHAR}, encryption_salt = #{encryptionSalt,jdbcType=VARCHAR}, connection_properties = #{connectionProperties,jdbcType=VARCHAR,typeHandler=com.insightdata.infrastructure.persistence.typehandler.JsonTypeHandler}, active = #{active,jdbcType=BOOLEAN}, description = #{description,jdbcType=VARCHAR}, last_sync_time = #{lastSyncTime,jdbcType=TIMESTAMP}, updated_at = NOW()
WHERE id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(1)
FROM data_source
WHERE name = #{name,jdbcType=VARCHAR}
