mapper.insert:
INSERT INTO data_source (
    name, type, host, port, database_name, username, encrypted_password, 
    encryption_salt, connection_properties, last_sync_time, active, description, 
    created_at, updated_at
) VALUES (
    #{name}, #{type}, #{host}, #{port}, #{databaseName}, #{username}, #{encryptedPassword}, 
    #{encryptionSalt}, #{connectionProperties, typeHandler=com.insightdata.infrastructure.persistence.typehandler.JsonMapTypeHandler}, 
    #{lastSyncTime}, #{active}, #{description}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT COUNT(*)
FROM data_source
WHERE name = #{name}
