mapper.insert:
INSERT INTO saved_query (
    name, description, data_source_id, natural_language_query, 
    generated_sql, parameters, is_public, created_by, created_at, updated_at
) VALUES (
    #{name}, #{description}, #{dataSourceId}, #{naturalLanguageQuery},
    #{generatedSql}, #{parameters, typeHandler=com.insightdata.infrastructure.persistence.typehandler.JsonMapTypeHandler},
    #{isPublic}, #{createdBy}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM saved_query 
WHERE data_source_id = #{dataSourceId}
ORDER BY updated_at DESC

SELECT * FROM saved_query 
WHERE data_source_id = #{dataSourceId}
AND is_public = #{isPublic}
ORDER BY updated_at DESC
